import { atom } from "recoil";

interface IToDo {
  toDo: [];
  doing: [];
  done: [];
}

export const themeAtom = atom<Boolean>({
  key: "theme",
  default: false,
});

export const toDoAtom = atom({
  key: "toDo",
  default: {
    july: [
      [
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
        { toDo: [], doing: [], done: [] },
      ],
    ],
    august: [],
  },
});
