import { atom } from "recoil";

export const themeAtom = atom<Boolean>({
  key: "theme",
  default: false,
});

export const toDoAtom = atom({
  key: "toDo",
  default: {
    july: Array.from(Array(7), () => Array(6).fill(null)),

    august: Array.from(Array(7), () => Array(6).fill(null)),
  },
});
