import styled from "styled-components";

import { useRecoilState, useRecoilValue, useSetRecoilState } from "recoil";
import { ThemeProvider, createGlobalStyle } from "styled-components";
import { DarkTheme, LightTheme } from "./theme";
import { themeAtom, toDoAtom } from "./atom";
import { faXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Calender } from "./utils/calender";
import React, { useState } from "react";

const GlobalStyle = createGlobalStyle`
  html, body, div, span, applet, object, iframe,
  h1, h2, h3, h4, h5, h6, p, blockquote, pre,
  a, abbr, acronym, address, big, cite, code,
  del, dfn, em, img, ins, kbd, q, s, samp,
  small, strike, strong, sub, sup, tt, var,
  b, u, i, center,
  dl, dt, dd, ol, ul, li,
  fieldset, form, label, legend,
  table, caption, tbody, tfoot, thead, tr, th, td,
  article, aside, canvas, details, embed, 
  figure, figcaption, footer, header, hgroup, 
  menu, nav, output, ruby, section, summary,
  time, mark, audio, video {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
  }
  /* HTML5 display-role reset for older browsers */
  article, aside, details, figcaption, figure, 
  footer, header, hgroup, menu, nav, section {
    display:block;

  }
  body {
    line-height: 1;
    color: ${(props) => props.theme.textColor}

  }
  ol, ul {
    list-style: none;

  }
  blockquote, q {
    quotes: none;

  }
  blockquote:before, blockquote:after,
  q:before, q:after {
    content: '';
    content: none;
  }
  table {
  border-collapse: collapse;
  border-spacing: 0;
  }

`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 7px solid black;
  padding: 15px;
`;
const Header = styled.header`
  color: black;
`;
const Title = styled.h2`
  font-size: 36px;
`;
const Main = styled.main`
  display: grid;
  grid-template-columns: 1fr 2fr;
  margin-top: 50px;
  border: 5px solid red;
  width: 1000px;
  max-height: 700px;
`;

const Section_R = styled.section`
  padding: 10px;
  background-color: ${(props) => props.theme.SectionColor_R};
`;

const Footer = styled.footer`
  margin-top: 30px;
  height: 100px;
  border: 3px solid green;
`;

////////////////////////////////////////

const Contents2 = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 30px;
  position: relative;
`;

const Date = styled.h1`
  font-size: 48px;
`;

const WeekList = styled.ul`
  display: flex;
  gap: 44px;
  font-size: 20px;
`;

const WeekItem = styled.li``;

const Days = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  /* grid-template-rows: repeat(6, 1fr); */
`;
const Day = styled.div`
  border: 1px solid black;
  padding: 30px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  top: 200px;
  width: 600px;
  height: 300px;
  padding: 20px;
  border-radius: 30px;
  background-color: ${(props) => props.theme.BasicColor};
`;
const Input = styled.input`
  height: 25px;
  width: 50%;
  border-radius: 5px;
  border: none;
  padding: 5px;
`;

interface ILocationProps {
  rowIdx: number | 0;
  colIdx: number | 0;
  month: string | "";
}

function App() {
  const [isDark, setIsDark] = useRecoilState(themeAtom);
  const [toDo, setToDo] = useRecoilState(toDoAtom);
  const [addToDo, setAddToDo] = useState("");
  const [dayLocation, setDayLocation] = useState<ILocationProps>();
  const [form, setForm] = useState(false);

  const onDarkMode = () => setIsDark((prev) => !prev);

  const onClick = (rowIdx: number, colIdx: number, month: string) => {
    setDayLocation(() => {
      return { rowIdx, colIdx, month };
    });
    setForm((prev) => !prev);
  };

  const onExit = () => setForm((prev) => !prev);

  const onSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setAddToDo("");
    setToDo((prev) => {
      if (dayLocation?.rowIdx === undefined) return prev;

      if (dayLocation.month === "july") {
        const copyJuly = JSON.parse(JSON.stringify(prev.july));
        copyJuly[dayLocation.rowIdx][dayLocation.colIdx].toDo.push(addToDo);
        return { august: prev.august, july: copyJuly };
      }

      if (dayLocation.month === "august") {
        const copyAugust = JSON.parse(JSON.stringify(prev.august));
        copyAugust[dayLocation.rowIdx][dayLocation.colIdx].toDo.push(addToDo);
        return { august: copyAugust, july: prev.july };
      }

      return prev;
    });
  };

  const onInput = (event: React.FormEvent<HTMLInputElement>) => {
    setAddToDo(event.currentTarget.value);
  };

  return (
    <>
      <ThemeProvider theme={isDark ? DarkTheme : LightTheme}>
        <Wrapper>
          <Header>
            <Title>TO-DO</Title>
          </Header>
          <Main>
            <Section_R>
              <Contents2>
                <Date>JULY 2022</Date>
                <WeekList>
                  {["MON", "TUE", "WED", "THU", "FRI", "SUN", "SAT"].map(
                    (v, idx) => (
                      <WeekItem key={idx}>{v}</WeekItem>
                    )
                  )}
                </WeekList>
                <hr style={{ width: "100%" }} />
                <Days>
                  {true
                    ? Calender.july.map((rowData, rowIdx) =>
                        rowData.map((colData, colIdx) => (
                          <Day
                            onClick={() => onClick(rowIdx, colIdx, "july")}
                            key={colIdx}
                          >
                            {colData.day}
                          </Day>
                        ))
                      )
                    : Calender.august.map((rowData, rowIdx) =>
                        rowData.map((colData, colIdx) => (
                          <Day
                            onClick={() => onClick(rowIdx, colIdx, "august")}
                            key={colIdx}
                          >
                            {colData.day}
                          </Day>
                        ))
                      )}
                </Days>
                {form ? (
                  <Form onSubmit={onSubmit}>
                    <FontAwesomeIcon
                      onClick={onExit}
                      style={{
                        position: "absolute",
                        top: "15px",
                        right: "15px",
                        fontSize: "30px",
                        cursor: "pointer",
                      }}
                      icon={faXmark}
                    ></FontAwesomeIcon>
                    <Input
                      placeholder="add a toDo..."
                      type="text"
                      value={addToDo}
                      onInput={onInput}
                    />
                  </Form>
                ) : null}
              </Contents2>
            </Section_R>
          </Main>
        </Wrapper>
        <Footer>footer</Footer>
        <GlobalStyle />
      </ThemeProvider>
    </>
  );
}

export default App;
