import { atom } from "recoil";

export const themeAtom = atom<Boolean>({
  key: "theme",
  default: false,
});

export const toDoAtom = atom({
  key: "toDo",
  default: {
    july: [...Array(7)].map((_) =>
      [...Array(6)].map((_) => ({
        toDo: [] as string[],
        doing: [] as string[],
        done: [] as string[],
      }))
    ),
    august: [...Array(7)].map((_) =>
      [...Array(6)].map((_) => ({
        toDo: [] as string[],
        doing: [] as string[],
        done: [] as string[],
      }))
    ),
  },
});
