import { atom } from "recoil";

export const themeAtom = atom<Boolean>({
  key: "theme",
  default: false,
});

export const toDoAtom = atom({
  key: "toDo",
  default: {
    july: [
      [{ toDo: [] }, { toDo: [] }, { toDo: [] }, { toDo: [] }, { toDo: [] }],
      [{ toDo: [] }, { toDo: [] }, { toDo: [] }, { toDo: [] }, { toDo: [] }],
    ],
    // Array.from(Array(7), () =>
    //   Array(6).fill({ toDo: [], doing: [], done: [] })
    // ),
    august: Array.from(Array(7), () =>
      Array(6).fill({ toDo: [], doing: [], done: [] })
    ),
  },
});
