import { atom } from "recoil";

export const themeAtom = atom<Boolean>({
  key: "theme",
  default: false,
});

export const toDoAtom = atom({
  key: "toDo",
  default: {
    july: Array.from(Array(7), () =>
      Array(6)
        .fill(0)
        .map((_) => {
          {
            return { toDo: [], doing: [], done: [] };
          }
        })
    ),

    august: Array.from(Array(7), () =>
      Array(6)
        .fill(0)
        .map((_) => {
          {
            return { toDo: [], doing: [], done: [] };
          }
        })
    ),
  },
});
